import axios from "axios";
const BASE_URL = "http://localhost:5000/api/";
const TOKEN =
  "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYyYzJlNzQ3ZDEzMzc1MWNjNjMzM2FhYSIsImlzQWRtaW4iOnRydWUsImlhdCI6MTY1ODA2NTU0NywiZXhwIjoxNjU4MzI0NzQ3fQ.fqEk226gOqlBOzr-GJcPLmza9pOws6BGPyP7mCEBgNk";
// const TOKEN = JSON.parse(
//   JSON.parse(localStorage.getItem("persist:root")).currentUser
// ).accessToken;

console.log(TOKEN);

export const publicRequest = axios.create({
  baseURL: BASE_URL,
});

export const userRequest = axios.create({
  baseURL: BASE_URL,
  headers: { token: `Bearer ${TOKEN}` },
});
